const styleSwitcherToggle=document.querySelector(".style-switcher-toggler");styleSwitcherToggle.addEventListener("click",()=>{document.querySelector(".style-switcher").classList.toggle("open")}),window.addEventListener("scroll",()=>{document.querySelector(".style-switcher").classList.contains("open")&&document.querySelector(".style-switcher").classList.remove("open")});const alternateStyles=document.querySelectorAll(".alternate-style");function setActiveStyle(t){alternateStyles.forEach(e=>{t===e.getAttribute("title")?e.removeAttribute("disabled"):e.setAttribute("disabled","true")})}const dayNight=document.querySelector(".day-night");dayNight.addEventListener("click",()=>{dayNight.querySelector("i").classList.toggle("fa-sun"),dayNight.querySelector("i").classList.toggle("fa-moon"),document.body.classList.toggle("dark")}),window.addEventListener("load",()=>{document.body.classList.contains("dark")?dayNight.querySelector("i").classList.add("fa-sun"):dayNight.querySelector("i").classList.add("fa-moon")});